func:
// Function node (Node-RED)
// ส่งเฉพาะฟิลด์สำคัญไปหน้าเว็บ: avg_temp, avg_hum, uptime_pct, timestamp, pin
// รวมถึงค่าสถานะใหม่: temp_status และ hum_status
// ไม่ส่ง status, ไม่ map MAC/room, ไม่แก้ topic
// ถ้ามี mac/room_id จะ pass-through ไปให้หน้าเว็บ map เอง

if (!msg || typeof msg !== 'object') {
    return null;
}
const p = msg.payload || {};

// ---------- helpers ----------
/**
 * แปลงค่าเป็นตัวเลข ถ้าแปลงไม่ได้จะคืนค่า null
 * @param {*} v ค่าที่ต้องการแปลง
 * @returns {number|null}
 */
function toNum(v) {
    if (v === null || v === undefined) return null;
    const n = Number(v);
    return Number.isFinite(n) ? n : null;
}

/**
 * ดึงค่าจาก object โดยใช้ key ที่กำหนด
 * @param {object} obj
 * @param {string[]} keys
 * @returns {*}
 */
function pick(obj, keys) {
    for (const k of keys) {
        if (obj?.[k] !== undefined && obj[k] !== null) {
            return obj[k];
        }
    }
    return undefined;
}

/**
 * จัดรูปแบบ MAC address ให้เป็นมาตรฐาน
 * @param {string} s
 * @returns {string|undefined}
 */
function normMAC(s) {
    const hex = String(s || '').toUpperCase().replace(/[^0-9A-F]/g, '');
    return hex.length === 12 ? hex.match(/.{2}/g).join(':') : undefined;
}

/**
 * แปลง timestamp เป็นรูปแบบ ISO 8601
 * @param {string|number} ts
 * @returns {string}
 */
function toISO(ts) {
    if (!ts) return new Date().toISOString();
    if (typeof ts === 'number') {
        const ms = ts < 1e11 ? ts * 1000 : ts; // epoch_s -> ms
        return new Date(ms).toISOString();
    }
    const d = new Date(ts);
    return isNaN(d) ? new Date().toISOString() : d.toISOString();
}

// ---------- normalize values ----------
const avgTemp = toNum(pick(p, ['avg_temp', 'temp', 'temperature', 't']));
const avgHum = toNum(pick(p, ['avg_hum', 'humidity', 'hum', 'rh']));
const minTemp = toNum(pick(p, ['min_temp', 'min_temperature']));
const maxTemp = toNum(pick(p, ['max_temp', 'max_temperature']));
const minHum = toNum(pick(p, ['min_hum', 'min_humidity']));
const maxHum = toNum(pick(p, ['max_hum', 'max_humidity']));
const tempStatus = pick(p, ['temp_status']);
const humStatus = pick(p, ['hum_status']);

let uptime = toNum(pick(p, ['uptime_pct', 'uptime_percent', 'uptime', 'up']));
if (uptime != null && uptime > 0 && uptime <= 1) {
    uptime = uptime * 100; // 0..1 -> %
}

const ts = pick(p, ['timestamp', 'time', 'ts', 'iso']);

const pin = pick(p, ['pin']);

// ---------- build minimal output ----------
const out = {};
if (avgTemp !== null) out.avg_temp = avgTemp;
if (avgHum !== null) out.avg_hum = avgHum;

// เพิ่มค่า min/max temp/hum
if (minTemp !== null) out.min_temp = minTemp;
if (maxTemp !== null) out.max_temp = maxTemp;
if (minHum !== null) out.min_hum = minHum;
if (maxHum !== null) out.max_hum = maxHum;

// เพิ่มค่าสถานะ
if (tempStatus !== undefined) out.temp_status = tempStatus;
if (humStatus !== undefined) out.hum_status = humStatus;

if (uptime !== null) out.uptime_pct = Math.max(0, Math.min(100, uptime));

out.timestamp = toISO(ts);

// เพิ่มค่า pin
if (pin !== undefined) out.pin = pin;

// pass-through mac / room_id ถ้ามี (หน้าเว็บจะไป map เอง)
const mac = normMAC(pick(p, ['mac', 'device_mac', 'eth_mac', 'wifi_mac']));
if (mac) out.mac = mac;
if (typeof p.room_id === 'string') out.room_id = p.room_id.toLowerCase();

// ---------- output ----------
msg.payload = out;
return msg;

// Learn room ↔ mac (แทนที่ของเดิมทั้งก้อน)
let p = msg.payload || {};
let room = String(p.room_id || '').toLowerCase();

function normMAC(s) {
    if (!s) return null;
    s = String(s).toUpperCase().replace(/[^0-9A-F]/g, '');
    if (s.length !== 12) return null;
    return s.match(/.{2}/g).join(':');
}
let mac = normMAC(p.mac);
if (!room || !mac) return null;

let room2mac = flow.get('room2mac') || {};
let mac2room = flow.get('mac2room') || {};
let room_seen = flow.get('room_seen') || {};

// 1) ถ้า MAC เคยอยู่ห้องอื่น → ย้ายบ้าน: ลบห้องเก่า
const prevRoom = mac2room[mac];
if (prevRoom && prevRoom !== room) {
    delete room2mac[prevRoom];
}

// 2) ถ้าห้องนี้เคยมี MAC อื่น → ให้ MAC เก่าออกก่อน
const prevMac = room2mac[room];
if (prevMac && prevMac !== mac) {
    delete mac2room[prevMac];
}

// 3) ตั้ง mapping ใหม่แบบ one-to-one
room2mac[room] = mac;
mac2room[mac] = room;
room_seen[room] = Date.now();

flow.set('room2mac', room2mac);
flow.set('mac2room', mac2room);
flow.set('room_seen', room_seen);
node.status({ fill: 'green', shape: 'dot', text: `${room} ↔ ${mac}` });
return null;

let states = flow.get('deviceStates') || {};
const MQTT_TIMEOUT_MS = 20000;

function now() { return Date.now(); }

function normalizeMac(s) {
    if (!s) return null;
    s = String(s).toUpperCase().replace(/[^0-9A-F]/g, '');
    if (s.length !== 12) return null;
    return s.match(/.{2}/g).join(':');
}

const ipMap = {
    '192.168.42.30': '94:B9:7E:CE:01:EF'
};

let mac = null;
let sourceType = null;

if ((msg.topic || '').includes("status")) {
    sourceType = "MQTT";
    mac = normalizeMac(msg.payload && msg.payload.mac);
    if (!mac) {
        const m = /^esp32\/([^/]+)\/status$/.exec(msg.topic || '');
        if (m && m[1]) mac = normalizeMac(m[1]);
    }
} else {
    sourceType = "Ping";
    mac = normalizeMac(ipMap[msg.topic]);
}

if (!mac) return null;

if (!states[mac]) {
    states[mac] = {
        pingOk: false,
        mqttOk: false,
        lastCombinedState: 'offline',
        ping_ms: null,
        lastMqttTs: 0
    };
}

const oldState = { ...states[mac] };

if (sourceType === "MQTT") {
    const st = (msg.payload && msg.payload.status || '').toString().toLowerCase();
    // อัปเดต mqttOk ตาม payload
    states[mac].mqttOk = (st === 'online');
    // เก็บ timestamp ล่าสุดที่ได้ MQTT สถานะ
    states[mac].lastMqttTs = now();
} else {
    // อัปเดตจาก Ping
    states[mac].pingOk = (msg.payload !== false);
    states[mac].ping_ms = (msg.payload !== false) ? Number(msg.payload) : oldState.ping_ms;
}

// ตรวจสอบ timeout ของ MQTT ทุกครั้งที่มีข้อความเข้ามา
if (states[mac].lastMqttTs === 0 || (now() - states[mac].lastMqttTs) > MQTT_TIMEOUT_MS) {
    states[mac].mqttOk = false;
}

const deviceState = states[mac];
const newCombinedState = (deviceState.pingOk && deviceState.mqttOk) ? 'online' : 'offline';

if (newCombinedState !== oldState.lastCombinedState || deviceState.ping_ms !== oldState.ping_ms) {
    states[mac].lastCombinedState = newCombinedState;
    flow.set('deviceStates', states);
    return { payload: { mac, status: newCombinedState, ping_ms: deviceState.ping_ms } };
}

return null;

// Get devices context
let devices = flow.get('devices') || {};

// Map IP to MAC and Room Name
const ipMap = {
  '192.168.42.30': { mac: '94:B9:7E:CE:01:EF', room: 'Server Room 1' }
};

const ip = msg.topic;
if (!ipMap[ip]) return null;

const mac = ipMap[ip].mac;
const room = ipMap[ip].room;
const now = Date.now();

let device = devices[mac] || { status: null, lastCheck: 0, room: room };

const newStatus = (msg.payload === false) ? 'offline' : 'online';

if (newStatus !== device.status) {
    device.status = newStatus;
    device.lastCheck = now;
    devices[mac] = device;
    flow.set('devices', devices);
    return {
        payload: {
            mac: mac,
            room: room,
            status: newStatus,
            event_time: new Date().toISOString()
        }
    };
}

device.lastCheck = now;
devices[mac] = device;
flow.set('devices', devices);
return null;

function toLocalSqlDate(d) {
  d = d || new Date();
  const local = new Date(d.getTime() - d.getTimezoneOffset() * 60000);
  return local.toISOString().slice(0,19).replace('T',' ');
}
let data = msg.payload;
let time = data.event_time
  ? toLocalSqlDate(new Date(data.event_time))
  : toLocalSqlDate();

msg.topic = `
  INSERT INTO esp_status_log
    (mac, room, status, time)
  VALUES
    (
      '${data.mac}',
      '${data.room.replace(/'/g,"''")}',
      '${data.status}',
      '${time}'
    );
`.replace(/\s+/g,' ');

return msg;

let d = msg.payload;

if (!d.mac || !d.timestamp) {
    node.error("❌ Missing mac or timestamp", d);
    return null;
}

msg.topic = "INSERT INTO `dht_22` (mac, pin, timestamp, min_temp, max_temp, avg_temp, min_hum, max_hum, avg_hum, temp_status,hum_status) VALUES (?,?,?,?,?,?,?,?,?,?,?);";

msg.payload = [
    d.mac,
    d.pin,
    d.timestamp,
    d.min_temp,
    d.max_temp,
    d.avg_temp,
    d.min_hum,
    d.max_hum,
    d.avg_hum,
    d.temp_status,
    d.hum_status
];

return msg;

let expected = ["25","26","32","33"];
let seen = Array.isArray(msg.payload) ? msg.payload : [];
let now = new Date();

let result = expected.map(pin => {
    let found = seen.find(r => r.pin === pin);
    if (!found || !found.last_seen) {
        return { pin, status: "OFFLINE", last_seen: null };
    }

    let last = new Date(found.last_seen);
    if (isNaN(last)) {
        return { pin, status: "OFFLINE", last_seen: found.last_seen };
    }

    let diff = (now.getTime() - last.getTime()) / 20000;
    return {
        pin,
        last_seen: found.last_seen,
        status: diff > 5 ? "OFFLINE" : "ONLINE"
    };
});

msg.payload = result;
msg.topic = 'pin_status_update';
return msg;

// Check reboot (single + all) — robust pending (by actionId & by mac)
const p = msg.payload || {};
const cmd = String(p.cmd || p.command || '').toLowerCase();
const actionId = String(p.actionId || '').trim();
const room = String(p.room_id || '').toLowerCase();

if (!actionId) {
  return [null, { payload: { actionId: null, status: 'error', message: 'missing actionId' } }];
}

// helpers
function normMAC(s){
  if(!s) return null;
  s = String(s).toUpperCase().replace(/[^0-9A-F]/g,'');
  if (s.length !== 12) return null;
  return s.match(/.{2}/g).join(':');
}

const now = Date.now();
const TTL_MS = 120000;      // mapping room↔mac สดภายใน 2 นาที
const ACK_TIMEOUT_MS = 60000;

// contexts
const pendingByAction = flow.get('reboot_pending_by_action') || {}; // actionId -> { targets:Set, seen:Set }
const pendingByMac    = flow.get('reboot_pending_by_mac')    || {}; // mac -> actionId
const timers          = flow.get('reboot_timers')             || {}; // actionId -> timer

// ========= Reboot All =========
if (cmd === 'reboot_all' || room === 'all' || Array.isArray(p.mac_list)) {
  let macs = Array.isArray(p.mac_list) ? p.mac_list.map(normMAC).filter(Boolean) : null;
  if (!macs || macs.length === 0) {
    const room2mac = flow.get('room2mac') || {};
    macs = Object.values(room2mac).map(normMAC).filter(Boolean);
  }
  if (!macs.length) {
    return [null, { payload: { actionId, status: 'error', message: 'no target MACs' } }];
  }

  // register pending (by actionId + by mac)
  pendingByAction[actionId] = { targets: new Set(macs), seen: new Set() };
  macs.forEach(m => { pendingByMac[m] = actionId; });

  // timeout per actionId
  if (timers[actionId]) clearTimeout(timers[actionId]);
  timers[actionId] = setTimeout(() => {
    const _pAct = flow.get('reboot_pending_by_action') || {};
    const _tim  = flow.get('reboot_timers') || {};
    const st = _pAct[actionId];
    if (st) {
      // cleanup mac->action
      st.targets.forEach(m => { 
        let map = flow.get('reboot_pending_by_mac') || {};
        if (map[m] === actionId) { delete map[m]; flow.set('reboot_pending_by_mac', map); }
      });
      delete _pAct[actionId];
      delete _tim[actionId];
      flow.set('reboot_pending_by_action', _pAct);
      flow.set('reboot_timers', _tim);
      node.send([null, { topic:'actions/ack', payload: { actionId, status:'timeout', message:'reboot_all partial/timeout' } }]);
    }
  }, ACK_TIMEOUT_MS);

  flow.set('reboot_pending_by_action', pendingByAction);
  flow.set('reboot_pending_by_mac', pendingByMac);
  flow.set('reboot_timers', timers);

  const outMsgs = macs.map(m => ({
    topic: `esp32/control/${m}/reboot`,
    payload: { cmd:'reboot', room_id:null, mac:m, actionId, ts:now }
  }));
  const ack = { payload:{ actionId, status:'accepted', message:`broadcast to ${macs.length} devices`} };
  return [outMsgs, ack];
}

// ========= Reboot Single =========
let mac = normMAC(p.mac);
if (!mac && room) {
  const room2mac = flow.get('room2mac') || {};
  const room_seen = flow.get('room_seen') || {};
  const m = normMAC(room2mac[room]);
  const ts = room_seen[room] || 0;
  if (m && (Date.now()-ts) <= TTL_MS) mac = m;
}
if (!mac) {
  return [null, { payload: { actionId, status:'error', message:`no fresh mapping for ${room}` } }];
}

pendingByAction[actionId] = { targets: new Set([mac]), seen: new Set() };
pendingByMac[mac] = actionId;

// timeout per actionId
if (timers[actionId]) clearTimeout(timers[actionId]);
timers[actionId] = setTimeout(() => {
  const _pAct = flow.get('reboot_pending_by_action') || {};
  const _pMac = flow.get('reboot_pending_by_mac') || {};
  const _tim  = flow.get('reboot_timers') || {};
  const st = _pAct[actionId];
  if (st) {
    st.targets.forEach(m => { if (_pMac[m] === actionId) delete _pMac[m]; });
    delete _pAct[actionId];
    delete _tim[actionId];
    flow.set('reboot_pending_by_mac', _pMac);
    flow.set('reboot_pending_by_action', _pAct);
    flow.set('reboot_timers', _tim);
    node.send([null, { topic:'actions/ack', payload:{ actionId, status:'timeout', message:`no response from ${mac}` } }]);
  }
}, ACK_TIMEOUT_MS);

flow.set('reboot_pending_by_action', pendingByAction);
flow.set('reboot_pending_by_mac', pendingByMac);
flow.set('reboot_timers', timers);

const mqttMsg = {
  topic: `esp32/control/${mac}/reboot`,
  payload: { cmd:'reboot', room_id:room||null, mac, actionId, ts:now }
};
const ack = { payload:{ actionId, status:'accepted', message:`reboot sent to ${mac}` } };
return [[mqttMsg], ack];

function normMAC(s) {
  if (!s) return null;
  s = String(s).toUpperCase().replace(/[^0-9A-F]/g, '');
  if (s.length !== 12) return null;
  return s.match(/.{2}/g).join(':');
}

const topic = String(msg.topic || '');

// ---------- parse payload ----------
let data = msg.payload;
if (typeof data === 'string') { try { data = JSON.parse(data); } catch { return null; } }
if (!data || typeof data !== 'object') return null;

const actionId = String(data.actionId || '').trim();
const status = String(data.status || (data.ok === true ? 'ok' : '')).toLowerCase() || 'ok';
const macPL = normMAC(data.mac);

let macTP = null, roomTP = null;
let m = topic.match(/^esp32\/ack\/([0-9A-F:]{17})\/[^/]+$/i);
if (m) macTP = normMAC(m[1]);
m = topic.match(/^esp32\/ack\/(room-[abcd])\/[^/]+$/i);
if (m) roomTP = m[1].toLowerCase();

let mac = macPL || macTP || null;
if (!mac && roomTP) {
  const room2mac = flow.get('room2mac') || {};
  mac = normMAC(room2mac[roomTP]);
}

// ---------- contexts ----------
let pByAct = flow.get('reboot_pending_by_action') || {}; // actionId -> {targets:Set, seen:Set}
let pByMac = flow.get('reboot_pending_by_mac') || {}; // mac -> actionId
let timers = flow.get('reboot_timers') || {};

const RECENT_TTL_MS = 30000; // กันซ้ำภายใน 30s หลังปิดงาน
let recent = flow.get('ack_recent') || {};        // actionId -> { ts:number, seen:Set }

// ---------- DE-DUP GUARD ----------
// ถ้า actionId นี้ถูกปิดไปแล้วเมื่อไม่นานนี้ ให้ drop เงียบๆ (ห้ามขึ้น unmatched)
if (actionId && !pByAct[actionId]) {
  const r = recent[actionId];
  if (r && (Date.now() - r.ts) <= RECENT_TTL_MS) {
    // ถ้าอยาก log เงียบๆ ให้ใช้ node.status สีเทา
    node.status({ fill: 'grey', shape: 'ring', text: `late/dup ACK: ${actionId}` });
    // บันทึก mac ที่เห็นไว้ (กันลูป)
    if (mac) { r.seen = r.seen || new Set(); r.seen.add(mac); recent[actionId] = r; flow.set('ack_recent', recent); }
    return null;
  }
}

// ---------- PRIORITY 1: match by actionId ----------
if (actionId && pByAct[actionId]) {
  const st = pByAct[actionId]; // {targets:Set, seen:Set}
  if (!st.seen) st.seen = new Set();
  if (mac) st.seen.add(mac);

  if (mac && pByMac[mac] === actionId) delete pByMac[mac];

  const done = st.seen.size;
  const total = st.targets.size;

  // ครบ → cleanup + บันทึก recent เพื่อกันซ้ำ
  if (done >= total) {
    if (timers[actionId]) { clearTimeout(timers[actionId]); delete timers[actionId]; }
    delete pByAct[actionId];
    recent[actionId] = { ts: Date.now(), seen: st.seen };
  }

  flow.set('reboot_pending_by_action', pByAct);
  flow.set('reboot_pending_by_mac', pByMac);
  flow.set('reboot_timers', timers);
  flow.set('ack_recent', recent);

  node.status({ fill: 'green', shape: 'dot', text: `ACK ${done}/${total}${mac ? ` from ${mac}` : ''}` });
  return {
    topic: 'actions/ack',
    payload: {
      actionId, status,
      message: data.message || (done >= total ? 'ok' : `ACK ${done}/${total}`),
      mac, done, total
    }
  };
}

// ---------- PRIORITY 2: match by mac ----------
if (mac && pByMac[mac]) {
  const aid = pByMac[mac];

  if (pByAct[aid]) {
    const st = pByAct[aid];
    if (!st.seen) st.seen = new Set();
    st.seen.add(mac);
    if (st.seen.size >= st.targets.size) {
      if (timers[aid]) { clearTimeout(timers[aid]); delete timers[aid]; }
      delete pByAct[aid];
      recent[aid] = { ts: Date.now(), seen: st.seen };
    } else {
      pByAct[aid] = st;
    }
  }
  delete pByMac[mac];

  flow.set('reboot_pending_by_action', pByAct);
  flow.set('reboot_pending_by_mac', pByMac);
  flow.set('reboot_timers', timers);
  flow.set('ack_recent', recent);

  node.status({ fill: 'green', shape: 'dot', text: `ACK by mac ${mac}` });
  return {
    topic: 'actions/ack',
    payload: { actionId: aid, status, message: data.message || 'rebooting', mac }
  };
}

// ---------- unmatched (เช็คอีกชั้น: late/dup หลังไม่มี pending) ----------
if (actionId) {
  // บันทึกไว้เป็น late เพื่อกันขึ้น unmatched รอบต่อไป
  recent[actionId] = { ts: Date.now(), seen: mac ? new Set([mac]) : new Set() };
  flow.set('ack_recent', recent);
  node.status({ fill: 'grey', shape: 'ring', text: `late ACK: ${actionId}` });
  return null; // ✨ ไม่ปล่อย unmatched ออกไปอีก
}

// ถ้าไม่มี actionId จริง ๆ ค่อยถือว่า unmatched เบา ๆ
node.status({ fill: 'yellow', shape: 'ring', text: `ACK unmatched: ${mac || 'n/a'}` });
return { topic: 'actions/ack', payload: { actionId: null, status, message: data.message || 'ACK unmatched', mac } };

// อ่าน query params
const q = msg.req && msg.req.query ? msg.req.query : {};
let { start, end, mac, room, pins } = q;

// helper: แปลงเวลา ISO → MySQL DATETIME (local)
function toSqlLocal(iso) {
    if (!iso) return null;
    const d = new Date(iso);
    if (isNaN(d)) return null;
    const tzFix = new Date(d.getTime() - d.getTimezoneOffset()*60000);
    return tzFix.toISOString().slice(0,19).replace('T',' ');
}

// default ช่วงเวลา: 24 ชั่วโมงล่าสุด
const now = new Date();
const defEnd = now.toISOString();
const defStart = new Date(now.getTime() - 24*60*60*1000).toISOString();

// ถ้าไม่กรอก ให้ใช้ดีฟอลต์ 24 ชม.
start = toSqlLocal(start || defStart);
end   = toSqlLocal(end   || defEnd);

// รองรับกรณีส่ง room มา → map เป็น mac จาก context เดิม (ถ้ามี)
if (!mac && room) {
    try {
        const r2m = flow.get('room2mac') || {};
        const key = String(room).toLowerCase();
        mac = r2m[key];
    } catch(e) {}
}

// ตรวจความถูกต้องขั้นต่ำ
if (!mac || !start || !end) {
    // ส่ง error กลับทันที (จะให้ MySQL ข้ามไป)
    msg.statusCode = 400;
    msg.payload = { error: "missing mac/start/end", hint: "GET /api/data?mac=AA:BB:CC:DD:EE:FF&start=ISO&end=ISO" };
    // ติดธงให้ http response โหนดปลายทางรู้ว่าไม่ต้องคิวรี
