// ถ้าไม่มีวันไหนถูกเลือกเลย ให้หยุด cron ทั้งหมด
if (!daysOfWeekStr) {
    node.warn("No days selected in schedule. Removing all cron jobs.");
    const removeMsg = { "payload": { "command": "remove-all" } };
    return [removeMsg, null]; // ส่ง msg ไปที่ output 1 เพื่อลบ, output 2 ไม่ต้องส่งอะไร
}

// สร้าง Job Array สำหรับ cron-plus จากเวลาที่ตั้งค่าไว้
const cronJobs = settings.schedule.times.map(time => {
    const [hour, minute] = time.split(':');
    // สร้าง Cron Expression เช่น "30 08 * * 1,2,3,4,5" (ทุก จ-ศ เวลา 08:30)
    const cronExpression = `${minute} ${hour} * * ${daysOfWeekStr}`;

    return {
        "name": `digest-${hour}-${minute}`, // ชื่อ job
        "expression": cronExpression,
        "type": "cron",
        "payload": { "type": "digest", "time": time } // ส่ง payload ไปกับ trigger ด้วย
    };
});

// สร้าง command สำหรับลบของเก่าและเพิ่มของใหม่
const removeAllCmd = { "payload": { "command": "remove-all" } };
const addJobsCmd = { "payload": { "command": "add", "jobs": cronJobs }};

// ส่ง msg ออก 2 port: 1.ลบของเก่า, 2.เพิ่มของใหม่
// เพื่อให้แน่ใจว่า schedule จะเป็นของใหม่ล่าสุดเสมอ
return [ removeAllCmd, addJobsCmd ];

// Function: eval_alert_v6.1 (Preserve Final Level on Close)

// --- CONTEXTS ---
var states = flow.get('alert_states') || {};
var rules = global.get('effectiveRuleMap') || {};
var deviceMap = flow.get('device_map', 'file') || {};
var now = new Date();
var now_iso = now.toISOString();

function getLocalSqlTimestamp() {
    const d = new Date();
    const tzOffset = d.getTimezoneOffset() * 60000;
    const localTime = new Date(d.getTime() - tzOffset);
    return localTime.toISOString().slice(0, 19).replace('T', ' ');
}
var now_sql = getLocalSqlTimestamp();

// --- INPUT PAYLOAD ---
var p = msg.payload;
if (!p || !p.mac || !p.sensor_name || !p.metric || p.value === undefined) {
    node.error("Invalid payload", msg);
    return null;
}

var mac = p.mac;
var sensor = p.sensor_name;
var metric = p.metric;
var value = Number(p.value);
var dedup_key = `${mac}|${sensor}|${metric}`;

// --- HELPERS ---
function getEffectiveRule(mac, sensor, metric) {
    var sensorKey = `${mac}|${sensor}|${metric}`;
    if (rules[sensorKey]) return rules[sensorKey];
    var overallKey = `${mac}|overall|${metric}`;
    if (rules[overallKey]) return rules[overallKey];
    return rules[`system_${metric}`] || {};
}
function numOrNull(x) {
    return (x === undefined || x === null || x === '') ? null : Number(x);
}
function determineRawLevel(value, rule) {
    var crit_max = numOrNull(rule.crit_max);
    var crit_min = numOrNull(rule.crit_min);
    var warn_max = numOrNull(rule.warn_max);
    var warn_min = numOrNull(rule.warn_min);
    if (crit_max !== null && value > crit_max) return 'critical';
    if (crit_min !== null && value < crit_min) return 'critical';
    if (warn_max !== null && value > warn_max) return 'warning';
    if (warn_min !== null && value < warn_min) return 'warning';
    return 'normal';
}
function uuidv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
}
function getRoomName(mac) {
    for (const key in deviceMap) {
        if (key.startsWith('room') && deviceMap[key] === mac) {
            const roomLetter = key.slice(-1).toUpperCase();
            return deviceMap[`name${roomLetter}`] || mac;
        }
    }
    return mac;
}

var rule = getEffectiveRule(mac, sensor, metric);
if (!rule.id) {
    return null;
}

rule.warn_open_consec = rule.warn_open_consec ?? 2;
rule.crit_open_consec = rule.crit_open_consec ?? 1;
rule.clear_consec = rule.clear_consec ?? 2;

var state = states[dedup_key] || {
    status: 'closed',
    level: 'normal',
    warn_count: 0,
    crit_count: 0,
    clear_count: 0,
    event_id: null,
};

var rawLevel = determineRawLevel(value, rule);

if (rawLevel === 'normal') {
    state.clear_count++;
    state.warn_count = 0;
    state.crit_count = 0;
} else if (rawLevel === 'warning') {
    state.warn_count++;
    state.crit_count = 0;
    state.clear_count = 0;
} else {
    state.crit_count++;
    state.warn_count = 0;
    state.clear_count = 0;
}

var outputs = [null, null, null];
var room = getRoomName(mac);

// A) RECOVERY: Close an existing open or acknowledged incident
if ((state.status === 'open' || state.status === 'ack') && rawLevel === 'normal' && state.clear_count >= rule.clear_consec) {

    const eventToClose = state.event_id;
    const originalStatus = state.status;
    const finalLevel = state.level; 

    // Update state in memory for the next event
    state.status = 'closed';
    state.level = 'normal';
    state.event_id = null;

    let updateSql;
    let updatePayload;
    let logNote;

    if (originalStatus === 'ack') {
        // Case 1: Alert was already acknowledged
        // Just change status to 'closed', set closed_at, and keep ack_by/ack_note
        logNote = 'System Recovery (post-acknowledgement)';
        updateSql = `
            UPDATE \`alert_events\` 
            SET \`status\`='closed', \`closed_at\`=?
            WHERE \`event_id\`=? AND \`status\` = 'ack';
        `; // <-- FIX: Added AND `status` = 'ack'
        updatePayload = [now_sql, eventToClose];

    } else { // originalStatus must be 'open'
        // Case 2: Alert was open but not yet acknowledged
        // Close the alert and mark it as closed by the system
        logNote = 'System Recovery';
        updateSql = `
            UPDATE \`alert_events\` 
            SET \`status\`='closed', \`closed_at\`=?, \`ack_by\`='system', \`ack_note\`=?
            WHERE \`event_id\`=? AND \`status\` = 'open';
        `;
        updatePayload = [now_sql, logNote, eventToClose];
    }

    // Create the INSERT statement for the log table (common for both cases)
    const logSql = `
        INSERT INTO \`alert_event_log\` (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`note\`, \`mac\`, \`sensor_name\`, \`metric\`) 
        VALUES (?, 'close', ?, ?, 'system', ?, ?, ?, ?, ?);
    `;
    const logPayload = [eventToClose, finalLevel, value, now_sql, logNote, mac, sensor, metric];

    outputs[0] = {
        topic: updateSql + logSql,
        payload: [...updatePayload, ...logPayload]
    };

    outputs[2] = { payload: { type: 'recovery', event_id: eventToClose, dedup_key: dedup_key, mac, sensor, metric, ts: now_iso } };
}

// B) OPEN NEW INCIDENT: Always a new row
else if (state.status === 'closed' && rawLevel !== 'normal') {
    var newLevel = null;
    if (rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) newLevel = 'critical';
    else if (rawLevel === 'warning' && state.warn_count >= rule.warn_open_consec) newLevel = 'warning';

    if (newLevel) {
        state.status = 'open';
        state.level = newLevel;
        state.event_id = uuidv4();

        outputs[0] = {
            topic: `
                INSERT INTO \`alert_events\` 
                    (\`event_id\`, \`dedup_key\`, \`mac\`, \`room_name\`, \`sensor_name\`, \`metric\`, \`level\`, \`trigger_value\`, \`status\`, \`rule_id\`, \`opened_at\`) 
                VALUES (?, ?, ?, ?, ?, ?, ?, ?, 'open', ?, ?);

                INSERT INTO \`alert_event_log\` 
                    (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'open', ?, ?, 'system', ?, ?, ?, ?);
            `,
            payload: [
                state.event_id, dedup_key, mac, room, sensor, metric, newLevel, value, rule.id, now_sql,
                state.event_id, newLevel, value, now_sql, mac, sensor, metric
            ]
        };
        outputs[1] = { payload: { type: 'open', level: newLevel, event_id: state.event_id, dedup_key: dedup_key, mac, sensor, metric, value, ts: now_iso } };
    }
}

// C) UPDATE EXISTING OPEN INCIDENT (Upgrade or Heartbeat)
else if (state.status === 'open' && rawLevel !== 'normal') {
    const eventToUpdate = state.event_id;
    if (state.level === 'warning' && rawLevel === 'critical' && state.crit_count >= rule.crit_open_consec) {
        state.level = 'critical';

        outputs[0] = {
            topic: `
                UPDATE \`alert_events\` 
                SET \`level\`='critical', \`trigger_value\`=?, \`room_name\`=? 
                WHERE \`event_id\`=?;

                INSERT INTO \`alert_event_log\` (\`event_id\`, \`action\`, \`level\`, \`value\`, \`actor\`, \`ts\`, \`mac\`, \`sensor_name\`, \`metric\`) 
                VALUES (?, 'upgrade', 'critical', ?, 'system', ?, ?, ?, ?);
            `,
            payload: [
                value, room, eventToUpdate,
                eventToUpdate, value, now_sql, mac, sensor, metric
            ]
        };
        outputs[1] = { payload: { type: 'upgrade', level: 'critical', event_id: eventToUpdate, dedup_key: dedup_key, mac, sensor, metric, value, ts: now_iso } };
    } else {
        // Just a heartbeat update
        outputs[0] = {
            topic: `UPDATE \`alert_events\` SET \`last_updated\`=? WHERE \`event_id\`=?;`,
            payload: [now_sql, eventToUpdate]
        };
    }
}

states[dedup_key] = state;
flow.set('alert_states', states);

if (outputs.some(o => o !== null)) {
    return outputs;
}
return null;

// Function: load_rules_to_global (FIXED VERSION)
const rules = msg.payload;
const deviceMap = flow.get('device_map', 'file') || {};

// ✨ START: จุดที่แก้ไข/เพิ่มเข้ามา ✨
// เราจะค้นหากฎของระบบ (system-level rules) ทั้งหมดที่เรามี
const systemTempRule = rules.find(r => r.scope === 'system' && r.metric === 'temp' && r.active) || {};
const systemHumRule = rules.find(r => r.scope === 'system' && r.metric === 'hum' && r.active) || {};
const systemConnRule = rules.find(r => r.scope === 'system' && r.metric === 'connectivity' && r.active) || {}; // เพิ่มบรรทัดนี้
// ✨ END: จุดที่แก้ไข/เพิ่มเข้ามา ✨


const roomRules = rules.filter(r => r.scope === 'room' && r.active);
const sensorRules = rules.filter(r => r.scope === 'sensor' && r.active);

const effectiveMap = {};

const inherit = (specific, room, system) => {
    const origins = {};
    const merged = {};
    const allKeys = new Set([...Object.keys(system), ...Object.keys(room), ...Object.keys(specific)]);

    for (const key of allKeys) {
        if (specific && specific[key] !== null && specific[key] !== undefined) {
            merged[key] = specific[key];
            origins[key] = 'sensor';
        } else if (room && room[key] !== null && room[key] !== undefined) {
            merged[key] = room[key];
            origins[key] = 'room';
        } else if (system && system[key] !== null && system[key] !== undefined) {
            merged[key] = system[key];
            origins[key] = 'system';
        }
    }
    merged.origins = origins;
    return merged;
};

const allMacs = Object.values(deviceMap).filter(val => typeof val === 'string' && val.includes(':'));

for (const mac of allMacs) {
    const sensorsForDevice = sensorRules.filter(r => r.mac === mac);

    const roomTempRule = roomRules.find(r => r.mac === mac && r.metric === 'temp') || {};
    const roomHumRule = roomRules.find(r => r.mac === mac && r.metric === 'hum') || {};

    const overallTempKey = `${mac}|overall|temp`;
    effectiveMap[overallTempKey] = inherit({}, roomTempRule, systemTempRule);

    const overallHumKey = `${mac}|overall|hum`;
    effectiveMap[overallHumKey] = inherit({}, roomHumRule, systemHumRule);

    for (const sensorRule of sensorsForDevice) {
        const key = `${mac}|${sensorRule.sensor_name}|${sensorRule.metric}`;
        if (sensorRule.metric === 'temp') {
            effectiveMap[key] = inherit(sensorRule, roomTempRule, systemTempRule);
        } else if (sensorRule.metric === 'hum') {
            effectiveMap[key] = inherit(sensorRule, roomHumRule, systemHumRule);
        }
    }
}

effectiveMap['system_temp'] = systemTempRule;
effectiveMap['system_hum'] = systemHumRule;

// ✨ START: จุดที่แก้ไข/เพิ่มเข้ามา ✨
effectiveMap['system_connectivity'] = systemConnRule; // เพิ่มบรรทัดนี้
// ✨ END: จุดที่แก้ไข/เพิ่มเข้ามา ✨

global.set('effectiveRuleMap', effectiveMap);
node.status({ fill: 'green', shape: 'dot', text: `Loaded ${Object.keys(effectiveMap).length} effective rules` });

return null;

// Function: Query alert event lists (V3 - Server-side Date Filtering)

// 1. อ่าน query parameters จากหน้าเว็บ
const q = (msg.req && msg.req.query) ? msg.req.query : {};
const statusFilter = (q.status || 'open,ack').toLowerCase();
const startDate = q.start; // Will be ISO string
const endDate = q.end;   // Will be ISO string

// Helper to convert ISO string to MySQL DATETIME format
function toSqlDatetime(isoString) {
    if (!isoString) return null;
    try {
        const d = new Date(isoString);
        // Adjust for timezone to get local time for DB
        const tzOffset = d.getTimezoneOffset() * 60000;
        const localTime = new Date(d.getTime() - tzOffset);
        return localTime.toISOString().slice(0, 19).replace('T', ' ');
    } catch (e) {
        return null;
    }
}

// 2. สร้าง SQL SELECT ที่ดึงข้อมูลเวลาทั้งหมดที่จำเป็น และสร้าง display_time ขึ้นมาใหม่
let sql = `
SELECT 
    event_id, mac, room_name, sensor_name, metric, level, status,
    opened_at, ack_at, closed_at, ack_by, ack_note, trigger_value,

    -- สร้างคอลัมน์เวลาหลักที่จะแสดงผล โดยเลือกตามสถานะของ event นั้นๆ
    CASE
        WHEN status = 'closed' THEN closed_at
        WHEN status = 'ack' THEN ack_at
        ELSE opened_at
    END AS display_time

FROM alert_events
`;

const conditions = [];
const params = [];

// 3. สร้างเงื่อนไข WHERE จาก status ที่ส่งมา
if (statusFilter) {
    const statuses = statusFilter.split(',').map(s => s.trim()).filter(Boolean);
    if (statuses.length > 0) {
        conditions.push(`status IN (${statuses.map(() => '?').join(',')})`);
        params.push(...statuses);
    }
}

// 4. เพิ่มเงื่อนไขการกรองด้วย MAC Address
if (q.mac) {
    conditions.push('mac = ?');
    params.push(q.mac);
}

// ✨ NEW: เพิ่มเงื่อนไขการกรองด้วยช่วงเวลา
const startSql = toSqlDatetime(startDate);
const endSql = toSqlDatetime(endDate);

if (startSql && endSql) {
    // เราจะกรองจากคอลัมน์ opened_at ซึ่งเป็นเวลาเริ่มต้นของทุก event
    conditions.push('opened_at BETWEEN ? AND ?');
    params.push(startSql, endSql);
}

if (conditions.length > 0) {
    sql += ' WHERE ' + conditions.join(' AND ');
}

// 5. จัดเรียงตาม display_time
sql += ' ORDER BY display_time DESC';

// 6. จัดการ Pagination (ยังคงไว้เหมือนเดิม)
const pageSize = parseInt(q.page_size, 10) || 50;
const page = parseInt(q.page, 10) || 1;
const offset = (page - 1) * pageSize;
sql += ` LIMIT ${pageSize} OFFSET ${offset}`;

// 7. ส่ง SQL และ Parameters
msg.topic = sql;
msg.payload = params;

return msg;

// รับ query params จากหน้าเว็บ
const q = msg.req.query;
const rules = global.get('effectiveRuleMap') || {};

// ตรวจสอบว่ามีพารามิเตอร์ที่จำเป็นครบหรือไม่
if (!q.mac || !q.sensor_name || !q.metric) {
    msg.statusCode = 400;
    msg.payload = { error: "mac, sensor_name, and metric are required query parameters" };
    return msg;
}

// สร้าง Key สำหรับแต่ละระดับ (Sensor, Room, System) ให้ถูกต้องตาม metric ที่ร้องขอมา
const sensorKey = `${q.mac}|${q.sensor_name}|${q.metric}`;
const roomKey = `${q.mac}|overall|${q.metric}`; // ✨ FIX: ทำให้เป็น Dynamic ตาม q.metric
const systemKey = `system_${q.metric}`;           // ✨ FIX: ทำให้เป็น Dynamic ตาม q.metric

// ค้นหา Rule ตามลำดับความสำคัญที่ถูกต้อง: Sensor -> Room -> System
let effectiveRule = rules[sensorKey] || rules[roomKey] || rules[systemKey] || {};

msg.payload = effectiveRule;
return msg;

// Expects msg.payload to be an array of rule objects
const rulesToUpdate = Array.isArray(msg.payload) ? msg.payload : [msg.payload];

if (rulesToUpdate.length === 0) {
    msg.statusCode = 400;
    msg.payload = { ok: false, error: "Payload must be a non-empty array of rule objects." };
    return msg;
}

// NOTE: This requires a UNIQUE key on (scope, mac, sensor_name, metric) in the DB table.
// ✨ จุดที่แก้ไข 1: เพิ่ม 'active' เข้าไปในรายการคอลัมน์
const sql = `INSERT INTO alert_rules (
    scope, mac, sensor_name, metric, active, 
    warn_min, warn_max, crit_min, crit_max, 
    warn_open_consec, crit_open_consec, clear_consec, 
    min_gap_seconds_warning, min_gap_seconds_critical, 
    repeat_minutes_warning, repeat_minutes_critical, 
    group_window_seconds, heartbeat_timeout_minutes
) VALUES ? 
ON DUPLICATE KEY UPDATE 
    active=VALUES(active), warn_min=VALUES(warn_min), warn_max=VALUES(warn_max), 
    crit_min=VALUES(crit_min), crit_max=VALUES(crit_max), 
    warn_open_consec=VALUES(warn_open_consec), crit_open_consec=VALUES(crit_open_consec), 
    clear_consec=VALUES(clear_consec), min_gap_seconds_warning=VALUES(min_gap_seconds_warning), 
    min_gap_seconds_critical=VALUES(min_gap_seconds_critical), repeat_minutes_warning=VALUES(repeat_minutes_warning), 
    repeat_minutes_critical=VALUES(repeat_minutes_critical), group_window_seconds=VALUES(group_window_seconds), 
    heartbeat_timeout_minutes=VALUES(heartbeat_timeout_minutes);`;

const values = rulesToUpdate.map(r => [
    r.scope, r.mac || null, r.sensor_name || null, r.metric || null,
    // ✨ จุดที่แก้ไข 2: จัดการค่า 'active'
    // ถ้าส่ง active: 0 มา ก็ใช้ 0, ถ้าไม่ได้ส่งมา ให้ default เป็น 1 (ใช้งาน)
    r.active !== undefined ? r.active : 1,
    r.warn_min, r.warn_max, r.crit_min, r.crit_max,
    r.warn_open_consec, r.crit_open_consec, r.clear_consec,
    r.min_gap_seconds_warning, r.min_gap_seconds_critical,
    r.repeat_minutes_warning, r.repeat_minutes_critical,
    r.group_window_seconds, r.heartbeat_timeout_minutes
]);

msg.topic = sql;
msg.payload = [values];

// We need to trigger a rule reload after this is done.
msg.reload_rules = true;

return msg;

// โค้ดสำหรับโหนด Function: Build SQL for Rules
msg.topic = "SELECT * FROM alert_rules WHERE active = 1 ORDER BY scope DESC";
return msg;

// Function: Adapt Payload for Alerts
// แปลงข้อมูลจาก ESP32 ให้อยู่ในรูปแบบที่ eval_alert_v2 ต้องการ
// จะสร้าง message แยกสำหรับแต่ละ metric (temp, hum)

const p = msg.payload;

// ตรวจสอบข้อมูลพื้นฐาน
if (!p || !p.mac || typeof p.pin === 'undefined') {
    // ไม่ใช่ข้อมูล sensor ที่ถูกต้อง, ไม่ต้องทำอะไรต่อ
    return null; 
}

const mac = p.mac;
// ใช้ pin เป็น sensor_name ไปก่อน ถ้ามีชื่อที่ map ไว้ก็สามารถดึงมาใช้ได้
const sensor_name = String(p.pin); 

const outputs = [];

// --- 1. ตรวจสอบข้อมูล Temperature ---
if (typeof p.avg_temp === 'number' && isFinite(p.avg_temp)) {
    const tempMsg = {
        topic: msg.topic, // ใช้ topic เดิม
        payload: {
            mac: mac,
            sensor_name: sensor_name,
            metric: 'temp', // ระบุว่าเป็น metric ของอุณหภูมิ
            value: p.avg_temp,
            ts: p.timestamp || new Date().toISOString()
        }
    };
    outputs.push(tempMsg);
}

// --- 2. ตรวจสอบข้อมูล Humidity ---
if (typeof p.avg_hum === 'number' && isFinite(p.avg_hum)) {
    const humMsg = {
        topic: msg.topic,
        payload: {
            mac: mac,
            sensor_name: sensor_name,
            metric: 'hum', // ระบุว่าเป็น metric ของความชื้น
            value: p.avg_hum,
            ts: p.timestamp || new Date().toISOString()
        }
    };
    outputs.push(humMsg);
}

// ถ้ามีข้อมูลที่แปลงแล้วอย่างน้อย 1 ตัว ให้ส่งออกไป
if (outputs.length > 0) {
    return [outputs]; // ส่งออกเป็น array ของ message
}

// ถ้าไม่มีข้อมูล temp หรือ hum เลย ก็ไม่ต้องส่งอะไรออกไป
return null;

// Function Node: Build SQL for GET /api/alert-rules

// 1. อ่าน query parameters จาก URL ที่หน้าเว็บส่งมา
const q = msg.req.query || {};

// 2. สร้าง SQL SELECT พื้นฐาน
let sql = 'SELECT * FROM alert_rules';
const conditions = [];
const params = [];

// 3. ต่อเงื่อนไข WHERE ตามพารามิเตอร์ที่ส่งมา
if (q.scope) {
    conditions.push('scope = ?');
    params.push(q.scope);
}
if (q.mac) {
    conditions.push('mac = ?');
    params.push(q.mac);
}
if (q.sensor_name) {
