// ✅ Function Node: System Summary (V2 - Includes temp_rise_per_room)

// --- Contexts ---
let liveData = flow.get('liveRoomData') || {};
let historyData = flow.get('historyRoomData') || {};
const deviceMap = flow.get('device_map', 'file') || {};

const BASELINE_SPAN_MS = 300000;   // 5 นาที
const BASELINE_STALE_MS = 3600000;  // 60 นาที

// --- MAC → RoomName map ---
const macToRoomInfo = {};
for (const key in deviceMap) {
  if (!Object.prototype.hasOwnProperty.call(deviceMap, key)) continue;
  if (key.startsWith('room')) {
    const mac = deviceMap[key];
    const idx = key.slice(-1).toUpperCase();
    const nameKey = `name${idx}`;
    const roomName = deviceMap[nameKey] || key;
    if (mac) macToRoomInfo[mac] = { name: roomName };
  }
}
function getRoomName(mac) {
  return macToRoomInfo[mac] ? macToRoomInfo[mac].name : mac;
}

// --- Helpers ---
function dewPointC(temp, rh) {
  if (typeof temp !== 'number' || typeof rh !== 'number' || !isFinite(temp) || !isFinite(rh) || rh <= 0) return null;
  const RH = Math.min(100, Math.max(0, rh));
  const a = 17.62, b = 243.12;
  const gamma = (a * temp) / (b + temp) + Math.log(RH / 100.0);
  return (b * gamma) / (a - gamma);
}

// --- Input & Update liveData ---
const now = Date.now();
const mac = msg.payload.mac || msg.payload.mac_address || null;
const pin = msg.payload.pin;

if (mac && pin === 'OVERALL') {
  const roomName = getRoomName(mac);
  if (roomName) {
    const t_avg = Number(msg.payload.avg_temp);
    const h_avg = Number(msg.payload.avg_hum);
    const t_max = Number(msg.payload.max_temp);
    const h_max = Number(msg.payload.max_hum);

