  hum_max: null, hum_max_room: null,
  dew_max: null, dew_max_room: null,
  temp_rise_max: null, temp_rise_max_room: null,
  temp_rise_per_room: {} // ✨ เพิ่ม key นี้เข้ามาใหม่
};

const rooms = Object.values(liveData);
if (rooms.length > 0) {
  for (const room of rooms) {
