    const curMac = room.mac;

    const tempForMax = room.temp_max_device ?? room.temp;
    if (typeof tempForMax === 'number' && (summary.temp_max === null || tempForMax > summary.temp_max)) {
      summary.temp_max = tempForMax;
      summary.temp_max_room = room.name;
    }

    const humForMax = room.hum_max_device ?? room.hum;
    if (typeof humForMax === 'number' && (summary.hum_max === null || humForMax > summary.hum_max)) {
      summary.hum_max = humForMax;
      summary.hum_max_room = room.name;
    }

    const dp = dewPointC(room.temp, room.hum);
    if (dp !== null && (summary.dew_max === null || dp > summary.dew_max)) {
      summary.dew_max = dp;
      summary.dew_max_room = room.name;
    }

    const base = historyData[curMac];
    if (base && typeof base.temp === 'number' && typeof room.temp === 'number') {
      const rise = room.temp - base.temp;
      // ✨ เก็บค่า rise ของห้องนี้ไว้
      summary.temp_rise_per_room[curMac] = rise;

      if (summary.temp_rise_max === null || rise > summary.temp_rise_max) {
        summary.temp_rise_max = rise;
        summary.temp_rise_max_room = room.name;
      }
    }
  }

  if (summary.temp_rise_max === null) {
    summary.temp_rise_max = 0;
    summary.temp_rise_max_room = summary.temp_max_room;
  }
}

