}

msg.reportInfo = {
    type: type,
    filename: `Report_${room.replace('-', '_')}_${new Date().toISOString().slice(0, 10)}.${type}`,
    maps: {
        roomNames: roomNameMap,
        pinNames: pinMap
    },
    params: { from, to, macs, sensors, room: room }
};

let sensorSql = `
SELECT 
    t.timestamp, t.mac, t.pin AS sensor_pin, t.avg_temp AS temperature, t.avg_hum AS humidity,
    (243.12 * (LN(GREATEST(1e-6, t.avg_hum/100)) + (17.62*t.avg_temp)/(243.12+t.avg_temp)) ) / (17.62 - (LN(GREATEST(1e-6, t.avg_hum/100)) + (17.62*t.avg_temp)/(243.12+t.avg_temp))) AS dew_point
FROM dht_22 t
WHERE t.mac IN (${macs.map(() => '?').join(',')}) AND t.timestamp BETWEEN ? AND ?`;
const sensorParams = [...macs, from, to];

if (sensors && room !== 'all') {
    const sensorList = sensors.split(',');
    sensorSql += ` AND t.pin IN (${sensorList.map(() => '?').join(',')})`;
    sensorParams.push(...sensorList);
}
sensorSql += ` ORDER BY t.timestamp ASC;`;

msg.topic = sensorSql;
msg.payload = sensorParams;

return msg;

msg.results = {
    sensors: msg.payload
};

const { from, to, macs } = msg.reportInfo.params;

const alertSql = `
SELECT 
    opened_at AS alert_time, mac, room_name, sensor_name, metric, level, trigger_value, 
    status, ack_by AS acknowledged_by, ack_at AS acknowledged_time, closed_at AS closed_time 
FROM alert_events 
WHERE mac IN (${macs.map(() => '?').join(',')}) AND opened_at BETWEEN ? AND ? 
ORDER BY opened_at ASC;`;
const alertParams = [...macs, from, to];

msg.topic = alertSql;
msg.payload = alertParams;

return msg;

const alerts = msg.payload;
const sensors = msg.results.sensors;
const reportInfo = msg.reportInfo;

if (!reportInfo) {
    node.error("Critical Error: reportInfo object is missing.", msg);
    msg.statusCode = 500;
    msg.payload = "Server error: Could not process report data.";
    return msg;
}

const { roomNames, pinNames } = reportInfo.maps;

const formatDate = (isoString) => {
    if (!isoString) return '';
    try {
        return new Date(isoString).toLocaleString('th-TH', { timeZone: 'Asia/Bangkok' });
    } catch (e) { return isoString; }
};

msg.formattedData = {
    sensors: sensors.map(s => {
        const sensor_name = (pinNames[s.mac] && pinNames[s.mac][`name_pin_${s.sensor_pin}`]) || `Pin ${s.sensor_pin}`;
        return {
            'Timestamp': formatDate(s.timestamp),
            'Room Name': roomNames[s.mac] || s.mac,
            'MAC Address': s.mac,
            'Sensor Pin': s.sensor_pin,
            'Sensor Name': sensor_name,
            'Temperature (°C)': (s.temperature === null || s.temperature === undefined) ? null : Number(Number(s.temperature).toFixed(2)),
            'Humidity (%)': (s.humidity === null || s.humidity === undefined) ? null : Number(Number(s.humidity).toFixed(2)),
            'Dew Point (°C)': (s.dew_point === null || s.dew_point === undefined) ? null : Number(Number(s.dew_point).toFixed(2)),

        };
    }),
    alerts: alerts.map(a => ({
        'Alert Time': formatDate(a.alert_time),
        'Room Name': a.room_name,
        'Sensor Name': a.sensor_name,
        'Metric': a.metric,
        'Level': a.level,
        'Value': a.trigger_value,
        'Status': a.status,
        'Ack By': a.acknowledged_by,
        'Ack Time': formatDate(a.acknowledged_time),
        'Closed Time': formatDate(a.closed_time)
    }))
};

return msg;

const { sensors, alerts } = msg.formattedData;
const { filename } = msg.reportInfo;

const csvSafe = (val) => {
    if (val === null || val === undefined) return '';
    let str = String(val);
    if (str.includes(',') || str.includes('"') || str.includes('\n')) {
        str = str.replace(/"/g, '""');
        return `"${str}"`;
    }
    return str;
};

let csvString = '';

const sensorHeaders = ['Timestamp', 'Room Name', 'MAC Address', 'Sensor Pin', 'Sensor Name', 'Temperature (°C)', 'Humidity (%)', 'Dew Point (°C)'];
csvString += '### SENSOR DATA ###\n';
csvString += sensorHeaders.join(',') + '\n';
sensors.forEach(s => {
    const row = sensorHeaders.map(h => csvSafe(s[h]));
    csvString += row.join(',') + '\n';
});

const alertHeaders = ['Alert Time', 'Room Name', 'Sensor Name', 'Metric', 'Level', 'Value', 'Status', 'Ack By', 'Ack Time', 'Closed Time'];
csvString += '\n### ALERT HISTORY ###\n';
csvString += alertHeaders.join(',') + '\n';
alerts.forEach(a => {
    const row = alertHeaders.map(h => csvSafe(a[h]));
    csvString += row.join(',') + '\n';
});

msg.headers = { 'Content-Disposition': `attachment; filename="${filename}"`, 'Content-Type': 'text/csv; charset=utf-8' };
msg.payload = csvString;

return msg;
